/* { dg-do compile } */
/* { dg-options "-O2 -fdump-tree-crc-details" } */

#include <stdint.h>

uint16_t crc16(uint16_t crc, uint8_t a, uint16_t polynom) {
  int i;
  crc ^= a;
  for (i = 0; i < 8; ++i) {
      if (crc & 1)
	crc = (crc >> 1) ^ polynom;
      else
	crc = (crc >> 1);
    }
  return crc;
}

